    let target = proxyConfig.host;
    if (proxyConfig.port) {
        target = target += ':' + proxyConfig.port + '/';
    }

    if (proxyConfig.path) {
        target = target + proxyConfig.path;
    }

    const compiler = webpack(devConfig);

    serve({
        port: process.env.PORT || 9009,
