      message: text,
      duration: 3000,
      position: 'bottom'
    });
    toast.present();
  }


  download(file){
    console.log(file);
    const fileTransfer = new Transfer();
    var uri= encodeURI(this.hostname + 'download/' + file);
    this.loading = this.loadingCtrl.create({
      content: 'Downloading...',
    });
    this.loading.present();

